import React, { useState } from 'react';
import './css/Join.css';
import axios from "axios";
import { getCookie, setCookie } from './utils/cookie';

const Login = () => {
  const [formData, setFormData] = useState({

    memberId: '',
    memberPassword: '',

  });

  const handleChange = (e) => {
    const { name, value } = e.target;

    setFormData({
        ...formData,
        [name]: value,
      });
  };

  const resetFormData = () => {
    setFormData({

      memberId: '',
      memberPassword: '',

    });
  };

  const memberLogin = () => {
    const requestData = {
      memberId: formData.memberId,
      memberPassword: formData.memberPassword,
    };

    axios
      .post('/login', requestData)
      .then((response) => {
        
        setCookie("memberId", response.headers.accesstoken, 1);
        setCookie("lastLoginTime", response.headers.lastlogintime, 1);

        window.location.href = '/';
      })
      .catch((error) => {
        console.error('login error:', error);
      });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('join form data:', formData);
    // send data
  };

  const handleGoBack = () => {
    // send data
    console.log('GO BACK BUTTON CLICK');
  };

  return (

    <div className="app-container">
      <form onSubmit={handleSubmit}>
        <table className='app-container'>
        <thead>
          <tr>
            <th colSpan="2">
            â€»LOGIN
            </th>
          </tr>
        </thead>
        <tbody>

            <tr>
              <th>ID</th>
              <td>
                <input
                  type="text"
                  name="memberId"
                  value={formData.memberId}
                  onChange={handleChange}
                />
              </td>
            </tr>
            <tr>
              <th>PASSWORD</th>
              <td>
                <input
                  type="password"
                  name="memberPassword"
                  value={formData.memberPassword}
                  onChange={handleChange}
                />
              </td>
            </tr>
          </tbody>
        </table>
        <button type="submit"
                onClick={() => {memberLogin();}}
        >  
          CONFIRM</button>

                  <button type="button" onClick={handleGoBack}>GO BACK</button>
      </form>
    </div>
  );
};

export default Login;
