import React, { useState } from 'react';
import './App.css';

import axios from "axios";

function generateOptions(start, end) {
  const options = [];

  for(let i = start; i <= end; i++){
    options.push(<option key={i} value={i}>{i}</option>);
  }

  return options;
}

const memberJoin = () => {

  axios.post(
    "bookstore/join",
    formData
  )

  .then(Response => {

    console.log('login confirm', response.data);


  })

}

const App = () => {
  const [formData, setFormData] = useState({
    memberName: '',
    memberEmailId: '',
    memberEmailAddress: '',
    emailDomain: '',
    memberId: '',
    memberPassword: '',
    memberPasswordRe: '',
    memberPhoneNumber1: '',
    memberPhoneNumber2: '',
    memberPhoneNumber3: '',
    memberBirthYear: '',
    memberBirthMonth: '',
    memberBirthDay: '',
    memberGender: '',
    address: {
      memberPostalCode: '',
      memberPostAddress: '',
      memberDetailedAddress: '',
    },
  });

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === "emailDomain") {
      if (value === "select") {
        setFormData((prevFormData) => ({
          ...prevFormData,
          emailDomain: value,
          memberEmailAddress: "",
        }));
      } else {
        setFormData((prevFormData) => ({
          ...prevFormData,
          emailDomain: value,
          memberEmailAddress: value,
        }));
      }
    } else if (name.includes("address.")) {
      const addressKeys = name.split('.');
      setFormData((prevFormData) => ({
        ...prevFormData,
        address: {
          ...prevFormData.address,
          [addressKeys[1]]: value,
        },
      }));
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('join form data:', formData);
    // send data
  };

  const handleCheckId = () => {
    // send data
    console.log('checkId BUTTON CLICK');
  };

  const handleGoBack = () => {
    // send data
    console.log('GO BACK BUTTON CLICK');
  };

  const handleSearchPostalCode = () => {
    // add search code 
    console.log('SEARCH BUTTON CLICK.');
  };

  return (

    <div className="app-container">
      <form onSubmit={handleSubmit}>
        <table className='app-container'>
        <thead>
          <tr>
            <th colSpan="2">
              JOIN
            </th>
          </tr>
        </thead>
        <tbody>
            <tr>
              <th>NAME</th>
              <td>
                <input
                  type="text"
                  name="memberName"
                  value={formData.memberName}
                  onChange={handleChange}
                />
                <span className='sign'></span>
                <button
                  type="checkId"
                  onClick={handleCheckId}
                >
                  CHECK ID
                </button>
              </td>
            </tr>
            <tr>
              <th>E-MAIL</th>
              <td>
                <input
                  type="text"
                  name="memberEmailId"
                  value={formData.memberEmailId}
                  onChange={handleChange}
                />
                <span className='sign'></span>
                <span className='sign'>@</span>
                <input
                  type="text"
                  name="memberEmailAddress"
                  value={formData.memberEmailAddress}
                  onChange={handleChange}
                  placeholder='直接入力します'
                />
                <span className='sign'></span>
                <select
                  name="emailDomain"
                  value={formData.emailDomain}
                  onChange={handleChange}
                >
                  <option value="">SELECT</option>
                  <option value="gmail.com">gmail.com</option>
                  <option value="yahoo.co.jp">yahoo.co.jp</option>
                  <option value="naver.com">naver.com</option>
                </select>
              </td>
            </tr>
            <tr>
              <th>ID</th>
              <td>
                <input
                  type="text"
                  name="memberId"
                  value={formData.memberId}
                  onChange={handleChange}
                  placeholder='(英)文字/数字'
                />
              </td>
            </tr>
            <tr>
              <th>PASSWORD</th>
              <td>
                <input
                  type="password"
                  name="memberPassword"
                  value={formData.memberPassword}
                  onChange={handleChange}
                  placeholder='8~16文字の英文、数字、特殊文字を組み合わせ'
                />
              </td>
            </tr>
            <tr>
              <th>PASSWORD RE</th>
              <td>
                <input
                  type="password"
                  name="memberPasswordRe"
                  value={formData.memberPasswordRe}
                  onChange={handleChange}
                />
              </td>
            </tr>
            <tr>
              <th>PHONE NUMBER</th>
              <td>
                <input
                  type="text"
                  name="memberPhoneNumber1"
                  value={formData.memberPhoneNumber1}
                  onChange={handleChange}
                />
                <span className='sign'></span>
                <span className='sign'>-</span>
                <input
                  type="text"
                  name="memberPhoneNumber2"
                  value={formData.memberPhoneNumber2}
                  onChange={handleChange}
                />
                <span className='sign'></span>
                <span className='sign'>-</span>
                <input
                  type="text"
                  name="memberPhoneNumber3"
                  value={formData.memberPhoneNumber3}
                  onChange={handleChange}
                />
              </td>
            </tr>
            <tr>
              <th>BIRTHDAY</th>
              <td>
                <select
                  name="memberBirthYear"
                  value={formData.memberBirthYear}
                  onChange={handleChange}
                  >
                  <option value="">Year</option>
                  {generateOptions(1900, new Date().getFullYear())}
                </select>
                &nbsp;
                <select
                  name="memberBirthMonth"
                  value={formData.memberBirthMonth}
                  onChange={handleChange}
                  >
                  <option value="">Month</option>
                  {generateOptions(1, 12)}
                </select>
                &nbsp;
                <select
                  name="memberBirthDay"
                  value={formData.memberBirthDay}
                  onChange={handleChange}
                  >
                  <option value="">Day</option>
                  {generateOptions(1, 31)}
                  </select>
              </td>
            </tr>
            <tr>
            <th>GENDER</th>
            <td>
              <label>
                <input
                  type="radio"
                  name="memberGender"
                  value="m"
                  checked={formData.memberGender === "m"}
                  onChange={handleChange}
                />
                M
              </label>
              <label>
                <input
                  type="radio"
                  name="memberGender"
                  value="f"
                  checked={formData.memberGender === "f"}
                  onChange={handleChange}
                />
                F
              </label>
            </td>
          </tr>
            <tr>
              <th row rowSpan={3}>ADDRESS</th>
              <td>
                <input
                  type="text"
                  name="address.memberPostalCode"
                  placeholder="POSTAL CODE"
                  value={formData.address.memberPostalCode}
                  onChange={handleChange}
                />
                <span className='sign'></span>
                <button
                  type="search"
                  onClick={handleSearchPostalCode}
                >
                  SEARCH
                </button>
              </td>
            </tr>
            <tr>
              <td>
                <input
                  type="text"
                  name="address.memberPostAddress"
                  placeholder="POST ADDRESS"
                  value={formData.address.memberPostAddress}
                  onChange={handleChange}
                />
              </td>
            </tr>
            <tr>
              <td>
                <input
                  type="text"
                  name="address.memberDetailedAddress"
                  placeholder="DETAILED ADDRESS"
                  value={formData.address.memberDetailedAddress}
                  onChange={handleChange}
                />
              </td>
            </tr>
          </tbody>
        </table>
        <button type="submit"
                onClick={() => {memberJoin();}}
        >  
          CONFIRM</button>
        <button type="button" onClick={handleGoBack}>GO BACK</button>
      </form>
    </div>
  );
};

export default App;
