import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    id: '',
    password: '',
    passwordRe: '',
    phoneNumber: '',
    birthday: '',
    gender: '',
    zipCode: '',
    address: '',
    detailAddress: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log('회원가입 폼 데이터:', formData);
  };

  return (
    <div className="app-container">
      <h1>JOIN</h1>
      <form onSubmit={handleSubmit}>
        <label>
          NAME:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
        </label>
        <label>
          E-MAIL:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
        </label>
        <label>
          ID:
          <input
            type="text"
            name="id"
            value={formData.id}
            onChange={handleChange}
          />
        </label>
        <label>
          PASSWORD:
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
          />
        </label>
        <label>
          PASSWORD CHECK:
          <input
            type="password"
            name="passwordRe"
            value={formData.passwordRe}
            onChange={handleChange}
          />
        </label>
        <label>
          PHONE NUMBER:
          <input
            type="tel"
            name="phoneNumber"
            value={formData.phoneNumber}
            onChange={handleChange}
          />
        </label>
        <label>
          BIRTHDAY :
          <input
            type="text"
            name="birthday"
            value={formData.birthday}
            onChange={handleChange}
          />
        </label>
        <label>
          GENDER:
          <select
            name="gender"
            value={formData.gender}
            onChange={handleChange}
          >
            <option value="">선택하세요</option>
            <option value="male">남성</option>
            <option value="female">여성</option>
          </select>
        </label>
        <label>
          우편번호:
          <input
            type="text"
            name="zipCode"
            value={formData.zipCode}
            onChange={handleChange}
          />
        </label>
        <label>
          주소:
          <input
            type="text"
            name="address"
            value={formData.address}
            onChange={handleChange}
          />
        </label>
        <label>
          상세 주소:
          <input
            type="text"
            name="detailAddress"
            value={formData.detailAddress}
            onChange={handleChange}
          />
        </label>
        <button type="submit">회원가입</button>
      </form>
    </div>
  );
};

export default App;
