import React, { useState } from 'react';
import './css/Join.css';
import axios from "axios";

function generateOptions(start, end) {
  const options = [];

  for(let i = start; i <= end; i++){
    options.push(<option key={i} value={i}>{i}</option>);
  }

  return options;
}

const Join = () => {
  const [formData, setFormData] = useState({
    memberName: '',
    memberEmailId: '',
    memberEmailAddress: '',
    emailDomain: '',
    memberId: '',
    memberPassword: '',
    memberPasswordRe: '',
    memberPhone1: '',
    memberPhone2: '',
    memberPhone3: '',
    memberBirthYear: '',
    memberBirthMonth: '',
    memberBirthDay: '',
    memberGender: '',
    address: {
      memberPostalCode: '',
      memberPostAddress: '',
      memberDetailedAddress: '',
    },
  });

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === "emailDomain") {
      if (value === "select") {
        setFormData((prevFormData) => ({
          ...prevFormData,
          emailDomain: value,
          memberEmailAddress: "",
        }));
      } else {
        setFormData((prevFormData) => ({
          ...prevFormData,
          emailDomain: value,
          memberEmailAddress: value,
        }));
      }
    } else if (name.includes("address.")) {
      const addressKeys = name.split('.');
      setFormData((prevFormData) => ({
        ...prevFormData,
        address: {
          ...prevFormData.address,
          [addressKeys[1]]: value,
        },
      }));
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const resetFormData = () => {
    setFormData({
      memberName: '',
      memberEmailId: '',
      memberEmailAddress: '',
      memberId: '',
      memberPassword: '',
      memberPasswordRe: '',
      memberPhone1: '',
      memberPhone2: '',
      memberPhone3: '',
      memberBirthYear: '',
      memberBirthMonth: '',
      memberBirthDay: '',
      memberGender: '',
      address: {
        memberPostalCode: '',
        memberPostAddress: '',
        memberDetailedAddress: '',
      },
    });
  };

  const memberJoin = () => {
    const requestData = {
      memberName: formData.memberName,
      memberEmailId: formData.memberEmailId,
      memberEmailAddress: formData.memberEmailAddress,
      memberId: formData.memberId,
      memberPassword: formData.memberPassword,
      memberPasswordRe: formData.memberPasswordRe,
      memberPhone1: formData.memberPhone1,
      memberPhone2: formData.memberPhone2,
