import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './css/Header.css'

const Header = () => {

  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // 여기서 세션 또는 다른 방법으로 로그인 상태를 확인하고 상태 업데이트
    const sessionValue = sessionStorage.getItem('isLoggedIn');
    setIsLoggedIn(sessionValue === '1');
  }, []);

  const handleLogout = () => {
    // 로그아웃 버튼 클릭 시 세션을 지우고 로그인 상태 업데이트
    sessionStorage.removeItem('isLoggedIn');
    setIsLoggedIn(false);
  };

  return (
    <header>

      <div className="left-menu">
        <Link to="/">HOME</Link>

        <Link to="/book">BOOK</Link>

        <Link to="/ebook">E-BOOK</Link>

        <Link to="/goods">GOODS</Link>

        <Link to="/bestseller">BESTSELLER</Link>
      </div>
      <div className="right-menu">
      {isLoggedIn ? (
          <>
            <Link to="/myaccount/checkpassword">MYPAGE</Link>
            <Link to="/cart">CART</Link>
            <button onClick={handleLogout}>LOGOUT</button>
          </>
        ) : (
          <>
            <Link to="/login">LOGIN</Link>
            <Link to="/join">JOIN</Link>
          </>
        )}
      </div>
    </header>
  );
};

export default Header;
