import React, { useState, useEffect } from 'react';
import './css/Join.css';
import axios from "axios";
import { getCookie } from './utils/cookie';

const MyAccountPwUpdate = () => {

  const [formData, setFormData] = useState({
    memberName: '',
    memberEmailId: '',
    memberEmailAddress: '',
    emailDomain: '',
    memberId: '',
    memberPassword: '',
    memberPasswordRe: '',
    memberPhone1: '',
    memberPhone2: '',
    memberPhone3: '',
    memberBirthYear: '',
    memberBirthMonth: '',
    memberBirthDay: '',
    memberGender: '',
    address: {
      memberPostalCode: '',
      memberPostAddress: '',
      memberDetailedAddress: '',
    },
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
      setFormData({
        ...formData,
        [name]: value,
      });
  };

  const resetFormData = () => {
    setFormData({
      memberId: '',
      memberPassword: '',
      memberPasswordRe: '',
      memberPasswordReNew: '',
    });
  };

  const confirm = () => {
    const requestData = {
          memberId: getCookie('memberId'),
          memberCookie: getCookie('memberCookie'),

      memberPassword: formData.memberPassword,
      memberPasswordRe: formData.memberPasswordRe,
      memberPasswordRe: formData.memberPasswordReNew,
    };

    axios
      .post('/myaccount/updatepassword', requestData)
      .then((response) => {
        console.log('update confirm', response.data);
        resetFormData();
        window.location.href = '/login';
      })
      .catch((error) => {
        console.error('updatepassword error:', error);
      });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('updatepassword form data:', formData);
    // send data
  };

  const handleGoBack = () => {
    // send data
    console.log('GO BACK BUTTON CLICK');
  };

  return (
    <div className="app-container">
      <form onSubmit={handleSubmit}>
        <table className='app-container'>
        <thead>
          <tr>
            <th colSpan="2">
            ※JOIN
            </th>
          </tr>
        </thead>
        <tbody>
            <tr>
              <th>PASSWORD</th>
              <td>
                <input
                  type="password"
                  name="memberPassword"
                  value={formData.memberPassword}
                  onChange={handleChange}
                />
              </td>
            </tr>
            <tr>
              <th>NEW PASSWORD</th>
              <td>
                <input
                  type="password"
                  name="memberPasswordRe"
                  value={formData.memberPasswordRe}
                  placeholder='8~16文字の英文、数字、特殊文字を組み合わせ'
                  onChange={handleChange}
                />
              </td>
            </tr>
            <tr>
              <th>RE ENTER NEW PASSWORD</th>
              <td>
                <input
                  type="password"
                  name="memberPasswordReNew"
                  value={formData.memberPasswordReNew}
                  placeholder='8~16文字の英文、数字、特殊文字を組み合わせ'
                  onChange={handleChange}
                />
              </td>
            </tr>

          </tbody>
        </table>
        <button type="submit"
                onClick={() => {confirm();}}
        >  
          CONFIRM</button>

        <button type="button" onClick={handleGoBack}>GO BACK</button>
      </form>
    </div>
  );
};

export default MyAccountPwUpdate;
