import React, { useState } from 'react';
import './App.css';
import Header from "./Footer/Footer"

function generateOptions(start, end) {
  const options = [];

  for(let i = start; i <= end; i++){
    options.push(<option key={i} value={i}>{i}</option>);
  }

  return options;
}

const App = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    emailAddress: '',
    emailDomain: '',
    id: '',
    password: '',
    passwordRe: '',
    phoneNumber1: '',
    phoneNumber2: '',
    phoneNumber3: '',
    birthYear: '',
    birthMonth: '',
    birthDay: '',
    gender: '',
    address: {
      postalCode: '',
      postAddress: '',
      detailedAddress: '',
    },
  });

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === "emailDomain") {
      if (value === "select") {
        setFormData((prevFormData) => ({
          ...prevFormData,
          emailDomain: value,
          emailAddress: "",
        }));
      } else {
        setFormData((prevFormData) => ({
          ...prevFormData,
          emailDomain: value,
          emailAddress: value,
        }));
      }
    } else if (name.includes("address.")) {
      const addressKeys = name.split('.');
      setFormData((prevFormData) => ({
        ...prevFormData,
        address: {
          ...prevFormData.address,
          [addressKeys[1]]: value,
        },
      }));
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('join form data:', formData);
    // send data
  };

  const handleGoBack = () => {
    // send data
    console.log('GO BACK BUTTON CLICK');
  };

  const handleSearchPostalCode = () => {
    // add search code 
    console.log('SEARCH BUTTON CLICK.');
  };

  return (
    <div className="app-container">
      <h1>JOIN</h1>
      <form onSubmit={handleSubmit}>
        <table>
        <tbody>
            <tr>
              <th>NAME</th>
              <td>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                />
                <span className='sign'></span>
                <button
                  type="checkId"
                  onClick={handleSearchPostalCode}
                >
                  CHECK ID
                </button>
              </td>
            </tr>
            <tr>
              <th>E-MAIL</th>
              <td>
                <input
                  type="text"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                />
                <span className='sign'></span>
                <span className='sign'>@</span>
                <input
                  type="text"
                  name="emailAddress"
                  value={formData.emailAddress}
                  onChange={handleChange}
                  placeholder='直接入力します'
                />
                <span className='sign'></span>
                <select
                  name="emailDomain"
                  value={formData.emailDomain}
                  onChange={handleChange}
                >
                  <option value="">SELECT</option>
                  <option value="gmail.com">gmail.com</option>
                  <option value="yahoo.co.jp">yahoo.co.jp</option>
                  <option value="naver.com">naver.com</option>
                </select>
              </td>
            </tr>
            <tr>
              <th>ID</th>
              <td>
                <input
                  type="text"
                  name="id"
                  value={formData.id}
                  onChange={handleChange}
                  placeholder='(英)文字/数字'
                />
              </td>
            </tr>
            <tr>
              <th>PASSWORD</th>
              <td>
                <input
                  type="password"
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  placeholder='8~16文字の英文、数字、特殊文字を組み合わせ'
                />
              </td>
            </tr>
            <tr>
              <th>PASSWORD RE</th>
              <td>
                <input
                  type="password"
                  name="passwordRe"
                  value={formData.passwordRe}
                  onChange={handleChange}
                />
              </td>
            </tr>
            <tr>
              <th>PHONE NUMBER</th>
              <td>
                <input
                  type="text"
                  name="phoneNumber1"
                  value={formData.phoneNumber1}
                  onChange={handleChange}
                />
                <span className='sign'></span>
                <span className='sign'>-</span>
                <input
                  type="text"
                  name="phoneNumber2"
                  value={formData.phoneNumber2}
                  onChange={handleChange}
                />
                <span className='sign'></span>
                <span className='sign'>-</span>
                <input
                  type="text"
                  name="phoneNumber3"
                  value={formData.phoneNumber3}
                  onChange={handleChange}
                />
              </td>
            </tr>
            <tr>
              <th>BIRTHDAY</th>
              <td>
                <select
                  name="birthYear"
                  value={formData.birthYear}
                  onChange={handleChange}
                  >
                  <option value="">Year</option>
                  {generateOptions(1900, new Date().getFullYear())}
                </select>
                &nbsp;
                <select
                  name="birthMonth"
                  value={formData.birthMonth}
                  onChange={handleChange}
                  >
                  <option value="">Month</option>
                  {generateOptions(1, 12)}
                </select>
                &nbsp;
                <select
                  name="birthDay"
                  value={formData.birthDay}
                  onChange={handleChange}
                  >
                  <option value="">Day</option>
                  {generateOptions(1, 31)}
                  </select>
              </td>
            </tr>
            <tr>
            <th>GENDER</th>
            <td>
              <label>
                <input
                  type="radio"
                  name="gender"
                  value="male"
                  checked={formData.gender === "male"}
                  onChange={handleChange}
                />
                Male
              </label>
              <label>
                <input
                  type="radio"
                  name="gender"
                  value="female"
                  checked={formData.gender === "female"}
                  onChange={handleChange}
                />
                Female
              </label>
            </td>
          </tr>
            <tr>
              <th row rowSpan={3}>ADDRESS</th>
              <td>
                <input
                  type="text"
                  name="address.postalCode"
                  placeholder="POSTAL CODE"
                  value={formData.address.postalCode}
                  onChange={handleChange}
                />
                <span className='sign'></span>
                <button
                  type="search"
                  onClick={handleSearchPostalCode}
                >
                  SEARCH
                </button>
              </td>
            </tr>
            <tr>
              <td>
                <input
                  type="text"
                  name="address.postAddress"
                  placeholder="POST ADDRESS"
                  value={formData.address.postAddress}
                  onChange={handleChange}
                />
              </td>
            </tr>
            <tr>
              <td>
                <input
                  type="text"
                  name="address.detailedAddress"
                  placeholder="DETAILED ADDRESS"
                  value={formData.address.detailedAddress}
                  onChange={handleChange}
                />
              </td>
            </tr>
          </tbody>
        </table>
        <button type="submit">CONFIRM</button>
        <button type="button" onClick={handleGoBack}>GO BACK</button>
      </form>
    </div>
  );
};

export default App;
