import React, { useState } from 'react';
import './css/Join.css';
import axios from "axios";

function generateOptions(start, end) {
  const options = [];

  for(let i = start; i <= end; i++){
    options.push(<option key={i} value={i}>{i}</option>);
  }

  return options;
}

const Login = () => {
  const [formData, setFormData] = useState({
    memberName: '',
    memberEmailId: '',
    memberEmailAddress: '',
    emailDomain: '',
    memberId: '',
    memberPassword: '',
    memberPasswordRe: '',
    memberPhone1: '',
    memberPhone2: '',
    memberPhone3: '',
    memberBirthYear: '',
    memberBirthMonth: '',
    memberBirthDay: '',
    memberGender: '',
    address: {
      memberPostalCode: '',
      memberPostAddress: '',
      memberDetailedAddress: '',
    },
  });

  const resetFormData = () => {
    setFormData({
      memberName: '',
      memberEmailId: '',
      memberEmailAddress: '',
      memberId: '',
      memberPassword: '',
      memberPasswordRe: '',
      memberPhone1: '',
      memberPhone2: '',
      memberPhone3: '',
      memberBirthYear: '',
      memberBirthMonth: '',
      memberBirthDay: '',
      memberGender: '',
      address: {
        memberPostalCode: '',
        memberPostAddress: '',
        memberDetailedAddress: '',
      },
    });
  };

  const memberJoin = () => {
    const requestData = {
      memberName: formData.memberName,
      memberEmailId: formData.memberEmailId,
      memberEmailAddress: formData.memberEmailAddress,
      memberId: formData.memberId,
      memberPassword: formData.memberPassword,
      memberPasswordRe: formData.memberPasswordRe,
      memberPhone1: formData.memberPhone1,
      memberPhone2: formData.memberPhone2,
      memberPhone3: formData.memberPhone3,
      memberBirthYear: formData.memberBirthYear,
      memberBirthMonth: formData.memberBirthMonth,
      memberBirthDay: formData.memberBirthDay,
      memberGender: formData.memberGender,
      memberPostalCode: formData.address.memberPostalCode,
      memberPostAddress: formData.address.memberPostAddress,
      memberDetailedAddress: formData.address.memberDetailedAddress,
    };

    axios
      .post('/login', requestData)
      .then((response) => {
        console.log('login confirm', response.data);
        resetFormData();
      })
      .catch((error) => {
        console.error('login error:', error);
      });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('join form data:', formData);
    // send data
  };

  const handleGoBack = () => {
    // send data
    console.log('GO BACK BUTTON CLICK');
  };

  return (

    <div className="app-container">
      <form onSubmit={handleSubmit}>
        <table className='app-container'>
        <thead>
          <tr>
            <th colSpan="2">
            ※LOGIN
            </th>
          </tr>
        </thead>
        <tbody>

            <tr>
              <th>ID</th>
              <td>
                <input
                  type="text"
                  name="memberId"
                  value={formData.memberId}
                />
              </td>
            </tr>
            <tr>
              <th>PASSWORD</th>
              <td>
                <input
                  type="password"
                  name="memberPassword"
                  value={formData.memberPassword}
                />
              </td>
            </tr>
          </tbody>
        </table>
        <button type="submit"
                onClick={() => {memberJoin();}}
        >  
          CONFIRM</button>

                  <button type="button" onClick={handleGoBack}>GO BACK</button>
      </form>
    </div>
  );
};

export default Login;
