import React, { useState } from 'react';
import './css/Join.css';
import axios from "axios";
import { getCookie } from './utils/cookie';

const MyPageCheckPw = () => {

    useEffect(() => {
        const memberAccountStatus = async () => {
    
          try {
            const requestData = {
              memberId: getCookie('memberId'),
              memberCookie: getCookie('memberCookie')
            };
    
            const response = await axios
              .post('/myaccount/checkpassword', requestData);
    
            let memberEmail = (response.data.result.memberEmail).split("@");

            let memberEmailId =memberEmail[0];
            let memberEmailAddress = memberEmail[1];
    
            setFormData({
              memberEmailId: memberEmailId,
              memberEmailAddress: memberEmailAddress,
            });
          } catch (error) {
            console.error('Error', error);
          }
        };
        memberAccountStatus();
      }, []); 


  const [formData, setFormData] = useState({
    memberEmailId: '',
    memberEmailAddress: '',
    memberPassword: '',
  });

  const confirm = () => {
    const requestData = {
      memberEmailId: formData.memberEmailId,
      memberEmailAddress: formData.memberEmailAddress,
      memberId: formData.memberId,
      memberPassword: formData.memberPassword,
    };

    axios
      .post('/myaccount/checkpassword', requestData)
      .then((response) => {
        console.log('login confirm', response.data);
        resetFormData();
        window.location.href = '/myaccount';
      })
      .catch((error) => {
        console.error('join error:', error);
      });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('join form data:', formData);
    // send data
  };

  const handleGoBack = () => {
    // send data
    console.log('GO BACK BUTTON CLICK');
  };

  return (

    <div className="app-container">
      <form onSubmit={handleSubmit}>
        <table className='app-container'>
        <thead>
          <tr>
            <th colSpan="2">
            ※CHECK PASSWORD
            </th>
          </tr>
        </thead>
        <tbody>
            <tr>
              <th>E-MAIL</th>
              <td>
                <input
                  type="text"
                  name="memberEmailId"
                  value={formData.memberEmailId}
                  readOnly
                />
                <span className="sign"></span>
                <span className="sign">@</span>
                <input
                  type="text"
                  name="memberEmailAddress"
                  value={formData.memberEmailAddress}
                  readOnly
                />
              </td>
            </tr>
            <tr>
              <th>PASSWORD</th>
              <td>
                <input
                  type="password"
                  name="memberPassword"
                  value={formData.memberPassword}
                  //onChange={handleChange}
                />
              </td>
            </tr>
          </tbody>
        </table>
        <button type="submit"
                onClick={() => {confirm();}}
        >  
          CONFIRM</button>

        <button type="button" onClick={handleGoBack}>GO BACK</button>
      </form>
    </div>
  );
};

export default MyPageCheckPw;
