import React, { useState } from 'react';
import './css/Join.css';
import axios from "axios";

const MyPageCheckPw = () => {


  const [formData, setFormData] = useState({
    memberEmailId: '',
    memberEmailAddress: '',
    memberId: '',
    memberPassword: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
  
    if (name === "emailDomain") {
      if (value === "select") {
        setFormData((prevFormData) => ({
          ...prevFormData,
          emailDomain: value,
          memberEmailAddress: "",
        }));
      } else {
        setFormData((prevFormData) => ({
          ...prevFormData,
          emailDomain: value,
          memberEmailAddress: value,
        }));
      }
    } else if (name.includes("address.")) {
      const addressKeys = name.split('.');
      setFormData((prevFormData) => ({
        ...prevFormData,
        address: {
          ...prevFormData.address,
          [addressKeys[1]]: value,
        },
      }));
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const resetFormData = () => {
    setFormData({
      memberEmailId: '',
      memberEmailAddress: '',
      memberPassword: ''
    });
  };

  const confirm = () => {
    const requestData = {
      memberEmailId: formData.memberEmailId,
      memberEmailAddress: formData.memberEmailAddress,
      memberId: formData.memberId,
      memberPassword: formData.memberPassword,
    };

    axios
      .post('/myaccount/checkpassword', requestData)
      .then((response) => {
        console.log('login confirm', response.data);
        resetFormData();
        window.location.href = '/myaccount';
      })
      .catch((error) => {
        console.error('join error:', error);
      });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('join form data:', formData);
    // send data
  };

  const handleCheckId = () => {
    // send data
    console.log('checkId BUTTON CLICK');
  };

  const handleGoBack = () => {
    // send data
    console.log('GO BACK BUTTON CLICK');
  };

  const handleSearchPostalCode = () => {
    // add search code 
    console.log('SEARCH BUTTON CLICK.');
  };

  return (

    <div className="app-container">
      <form onSubmit={handleSubmit}>
        <table className='app-container'>
        <thead>
          <tr>
            <th colSpan="2">
            ※CHECK PASSWORD
            </th>
          </tr>
        </thead>
        <tbody>
        <tr>
              <th>E-MAIL</th>
              <td>
                <input
                  type="text"
                  name="memberEmailId"
                  value={formData.memberEmailId}
                  readOnly
                />
                <span className="sign"></span>
                <span className="sign">@</span>
                <input
                  type="text"
                  name="memberEmailAddress"
                  value={formData.memberEmailAddress}
                  readOnly
                />
              </td>
            </tr>
            <tr>
              <th>PASSWORD</th>
              <td>
                <input
                  type="password"
                  name="memberPassword"
                  value={formData.memberPassword}
                  onChange={handleChange}
                />
              </td>
            </tr>
          </tbody>
        </table>
        <button type="submit"
                onClick={() => {confirm();}}
        >  
          CONFIRM</button>

        <button type="button" onClick={handleGoBack}>GO BACK</button>
      </form>
    </div>
  );
};

export default MyPageCheckPw;
